import React, { Component } from 'react';

class Number extends Component {
    state = {
        name : 'Moon'
    }
    clickHandle = () => {
        this.state.name = 'Sun'
    }

    render() {
        const { name } = this.state.name;
        return (
            <>
            <h1>{name}</h1>
            <button className="button" onClick={this.clickHandle}>+</button>
            </>
        );
    }
}

export default Number;
