/*eslint-disable*/
import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Button } from "@material-ui/core";

const emailPattern = /^[0-9a-zA-Z]([-_.]?[0-9a-zA-Z])*@[0-9a-zA-Z]([-_.]?[0-9a-zA-Z])*.[a-zA-Z]{2,3}$/i;
const idPattern = /^[A-Za-z]{1}[A-Za-z0-9]{3,19}$/;
const pwPattern = /^(?=.*[a-zA-Z])(?=.*[!@#$%^*+=-])(?=.*[0-9]).{8,16}$/;
const userNamePattern = /^[가-힣]{2,4}$/;
const phonePattern = /(^02.{0}|^01.{1}|[0-9]{3})([0-9]+)([0-9]{4})/g;

const SignUp = () => {
  let data = [];
  let [userInfo, setUserInfo] = useState({
    email: null,
    userName: null,
    userId: null,
    password: null,
  });
  const [email, setEmail] = useState(null);
  const [userName, setUserName] = useState(null);
  const [userID, setUserID] = useState(null);
  const [password, setPassword] = useState(null);

  const handleUserInfo = (value, name) => {
    let copyUserInfo = { ...userInfo };

    if (name === "email") {
      console.log(emailPattern.test(value));
      if (
        emailPattern.test(value) === true ||
        phonePattern.test(value) === true
      ) {
        copyUserInfo.email = value;
        setUserInfo({ ...copyUserInfo });
        setEmail("✓");
      } else {
        if (emailPattern.test(value) === false) {
          setEmail("❌");
        }
        if (phonePattern.test(value) === false) {
          setEmail("❌");
        }
      }
    } else if (name === "userName") {
      if (userNamePattern.test(value) === true) {
        copyUserInfo.userName = value;
        setUserInfo({ ...copyUserInfo });
        setUserName("✓");
      } else {
        setUserName("❌");
      }
    } else if (name === "userId") {
      if (idPattern.test(value)) {
        copyUserInfo.userId = value;
        setUserInfo({ ...copyUserInfo });
        setUserID("✓");
      } else {
        setUserID("❌");
      }
    } else if (name === "password") {
      if (pwPattern.test(value) === true) {
        copyUserInfo.password = value;
        setUserInfo({ ...copyUserInfo });
        setPassword("✓");
      } else {
        setPassword("❌");
      }
    }
  };

  const send = (userInfo) => {
    fetch("http://localhost:3000", {
      method: "POST",
      body: JSON.stringify(userInfo),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((json) => json);
  };

  const handleSubmit = (e) => {
    // 서버에 전송 후, userInfo를 초기화
    send(userInfo);
  };

  return (
    <div className="body">
      <div className="stage">
        <div className="title">Instagram</div>
        <div className="smallTitle">
          <div>Sign up to see photos and videos</div>
          <div>from your friends.</div>
        </div>
        <Button color="primary">Log in with Facebook</Button>
        <div>------ 또는 ------</div>
        <form className="form" onSubmit={handleSubmit} name="loginform">
          <div>
            <input
              placeholder="휴대폰 번호 또는 이메일 주소"
              name="email"
              onChange={(e) => {
                handleUserInfo(e.target.value, e.target.name);
              }}
            ></input>
            <span>{email}</span>
          </div>
          <div>
            <input
              placeholder="성명"
              name="userName"
              onChange={(e) => {
                handleUserInfo(e.target.value, e.target.name);
              }}
            ></input>
            <span>{userName}</span>
          </div>
          <div>
            <input
              placeholder="사용자 이름"
              name="userId"
              onChange={(e) => {
                handleUserInfo(e.target.value, e.target.name);
              }}
            ></input>
            <span>{userID}</span>
          </div>
          <div>
            <input
              placeholder="비밀번호"
              name="password"
              onChange={(e) => {
                handleUserInfo(e.target.value, e.target.name);
              }}
            ></input>
            <span>{password}</span>
          </div>

          <button type="submit">가입</button>
          {data}
        </form>
        <div>
          가입하면 instagram의 약관, 데이터 정책 및 쿠키 정책에 동의하게 됩니다.
        </div>
        <div>
          계정이 있으신가요? <Link to="/">로그인</Link>
        </div>
        <div>앱을 다운로드하세요.</div>
        <div className="downLoadLink"></div>
        <footer>개요</footer>
      </div>
    </div>
  );
};

export default SignUp;
