import React, {useState} from 'react'
import { Link } from "react-router-dom";

//목표: 정보들을 전송 시, 데이터 안의 정보들과 비교하여 ID 값과 비밀번호 값이 같다면, 로그인 성공 화면을 보여준다.

// 1. 브라우저에서 전송을 한다.
// 2. 전송된 데이터를 dataBase와 비교를 한다.


let dataBase = [{"email" : "abcde@gmail.com", "userName" : "Kim", "userId" : "abcde", "password" : "123456" }];
const SignIn = () => {

    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')

    const handleSubmit = e => {
        e.preventDefault();
        if(email === dataBase[0].email && password === dataBase.password) {
          console.log('로그인 성공하셨습니다.')
        } else {
          console.log('잘못 입력하셨습니다.')
        }
    }

    const handleChange = (e) => {
        if(e.target.name === 'email') {
            setEmail(e.target.value)
        } else if(e.target.name === 'password') {
            setPassword(e.target.value)
        }
    }

    return (
        <div className="body">
             <div className="stage">  {/* 회원가입 폼 감싸있는 태그 */}
                 <div className="title">Instagram</div>
                 <form className="form" onSubmit={handleSubmit} name = "loginform" action = "./Main.jsx" >
                     <input placeholder="전화번호, 사용자 이름 또는 이메일" name="email" onChange ={((e) => {handleChange(e)})}></input>
                     <input placeholder="비밀번호" name="password" onChange={((e) => {handleChange(e)})}></input>
                     <button type="submit" >로그인</button>

                     {/* {data} */}
                 </form>
                <div>-------------------또는------------------</div>
                 <div>Log in with Facebook</div>
                 <div>Forgot password?</div>
                 <div> Don't have an account?
                     <Link to ="/SignUp">Sign up</Link>
                 </div>
                 <div className="downLoadLink">
                     <button>App Store에서 다운로드 하기</button><button>구글 플레이</button>
                 </div>
                 <footer>개요</footer>  
             </div>

         </div>
     )
}

export default SignIn
